import React, { useCallback, useState } from "react";
import axios from "axios";

const Post: React.FC = () => {
  const [title, setTitle] = useState("");
  const [image, setImage] = useState<File>();

  const selectImage = useCallback((e: any) => {
    const selectedImage = e.target.files[0];
    setImage(selectedImage);
  }, []);

  const createFormData = () => {
    const formData = new FormData();
    if (!image) return;
    formData.append("article[title]", title);
    formData.append("article[image]", image);
    return formData;
  };

  const sendFormData = async () => {
    const url = "localhost:3001/articles";
    const data = await createFormData(); //formdataが作成されるのを待つ
    const config = {
      headers: {
        "content-type": "multipart/form-data",
      },
    };
    axios
      .post(url, data, config)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div>
      　　　<h1>投稿</h1>
      <label>
        タイトル：
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </label>
      <input type="file" onChange={(e) => selectImage(e)} />
      <button onClick={sendFormData}>送信</button>
    </div>
  );
};

export default Post;
