import React, { useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "App";
import { getTTFB } from "web-vitals";

const ShowBook: React.FC = () => {
  const [bookId, setBookId] = useState("");
  const [bookTitle, setBookTitle] = useState("");
  const [bookName, setBookName] = useState("");
  const [labelUrl, setLabelUrl] = useState("");
  const [todos, setTodos] = useState<Todo[]>([]);

  type Todo = {
    inputValue: string;
    id: number;
    checked: boolean;
  };

  const getBookUrl = () => {
    if (!bookId) return;
    const url = `http://localhost:3001/books/${bookId}`;
    axios.get(url).then((response) => {
      const url = response.data.book.label.url;
      setLabelUrl(`${url}`);
      const title = response.data.book.title;
      setBookTitle(title);
      const name = response.data.book.name;
      setBookName(name);
    });
  };
  return (
    <div>
      <h1>書籍を検索する</h1>
      <input
        type="number"
        value={bookId}
        onChange={(e) => setBookId(e.target.value)}
      />
      <button onClick={getBookUrl}>検索</button>
      <ul>
        {articles.map((article) => (
          <li key={article.id}>
            <h3>{article.title}</h3>
            <p>{replaceCreatedAt(article.created_at)}</p>
            <p>投稿者:{article.user_id}</p>
            <div>{article.content}</div>
          </li>
        ))}
      </ul>
      <p>タイトル:{bookTitle}</p>
      {labelUrl && <img src={labelUrl} width={200} />}
      <h2>名前: {bookName}</h2>
    </div>
  );
};

export default ShowBook;
