import axios from "axios";
import { useState } from "react";

import { Book } from "components/pages/posttype";

export const useAllUsers = () => {
  const [books, setUsers] = useState<Array<Book>>([]);

  const getAllUsers = () => {
    // GET メソッド
    axios.get<Array<Book>>("/books").then((res) => {
      const data = res.data.map((books) => ({
        name: books.name,
        title: books.title,
        image: books.label,
        id: books.id,
      }));
      setUsers(data); // stateの更新
    });
  };

  return { getAllUsers, books }; // 別コンポーネントで使えるように'return'します
};
